<?php// ////////////////////////////////////////////////// type categorie// ////////////////////////////////////////////////class Categorie extends DB { public $id; public $titreFr; public $titreEn; public $titreAr; public $descriptionFr; public $descriptionEn; public $descriptionAr; public $image; public $publication; public function getId()  {  return $this->id;  } public function getTitreFr()  {  return $this->titreFr;  } public function getTitreEn()  {  return $this->titreEn;  } public function getTitreAr()  {  return $this->titreAr;  } public function getDescriptionFr()  {  return $this->descriptionFr;  } public function getDescriptionEn()  {  return $this->descriptionEn;  } public function getDescriptionAr()  {  return $this->descriptionAr;  } public function getImage()  {  return $this->image;  } public function getPublication()  {  return $this->publication;  } public function setId($value)  {  $this->id = $value;  } public function setTitreFr($value)  {  $this->titreFr = $value;  } public function setTitreEn($value)  {  $this->titreEn = $value;  } public function setTitreAr($value)  {  $this->titreAr = $value;  } public function setDescriptionFr($value)  {  $this->descriptionFr = $value;  } public function setDescriptionEn($value)  {  $this->descriptionEn = $value;  } public function setDescriptionAr($value)  {  $this->descriptionAr = $value;  } public function setImage($value)  {  $this->image = $value;  } public function setPublication($value)  {  $this->publication = $value;  } public function setCategorie($id, $titreFr, $titreEn, $titreAr, $descriptionFr, $descriptionEn, $descriptionAr, $image, $publication)  {  $this->id = $id;  $this->titreFr = $titreFr;  $this->titreEn = $titreEn;  $this->titreAr = $titreAr;  $this->descriptionFr = $descriptionFr;  $this->descriptionEn = $descriptionEn;  $this->descriptionAr = $descriptionAr;  $this->image = $image;  $this->publication = $publication;  } public function getSQL()  {  $str = sprintf(" titreFr='%s'", mysqli_real_escape_string($this->connect() , $this->getTitreFr()));  $str.= sprintf(", titreEn='%s'", mysqli_real_escape_string($this->connect() , $this->getTitreEn()));  $str.= sprintf(", titreAr='%s'", mysqli_real_escape_string($this->connect() , $this->getTitreAr()));  $str.= sprintf(", descriptionFr='%s'", mysqli_real_escape_string($this->connect() , $this->getDescriptionFr()));  $str.= sprintf(", descriptionEn='%s'", mysqli_real_escape_string($this->connect() , $this->getDescriptionEn()));  $str.= sprintf(", descriptionAr='%s'", mysqli_real_escape_string($this->connect() , $this->getDescriptionAr()));  $str.= sprintf(", image='%s'", mysqli_real_escape_string($this->connect() , $this->getImage()));  $str.= sprintf(", publication='%b'", mysqli_real_escape_string($this->connect() , $this->getPublication()));  return ($str);  } public function updateToDB()  {  $query = "update " . $_SESSION['pfx'] . "_categorie set ";  $query.= $this->getSQL();  $query.= sprintf(" where id = %d", $this->id);  return $this->connect()->query($query);  } public function saveToDB()  {  $query = "insert into " . $_SESSION['pfx'] . "_categorie set ";  $query.= $this->getSQL();  return $this->connect()->query($query);  } public function deleteImageFromDB($id)  {  $query = "update " . $_SESSION['pfx'] . "_categorie set image = ''";  $query.= sprintf(" where id = %d", mysql_real_escape_string($id));  return $this->connect()->query($query);  } public function deleteFromDB($id)  {  $query = "delete from " . $_SESSION['pfx'] . "_categorie ";  $query.= sprintf(" where id = '%d'", ($id));  return $this->connect()->query($query);  } public function getFromDB($id)  {  $res = $this->connect()->query("select * from " . $_SESSION['pfx'] . "_categorie where id = '$id'");  while ($fetch = $res->fetch_assoc())   {   $row[] = $fetch;   $this->setCategorie($fetch["id"], $fetch["titreFr"], $fetch["titreEn"], $fetch["titreAr"], $fetch["descriptionFr"], $fetch["descriptionEn"], $fetch["descriptionAr"], $fetch["image"], $fetch["publication"]);   }  } public function publishToDB($id, $pub)  {  $query = sprintf("update " . $_SESSION['pfx'] . "_categorie set publication = '%b' where id = '%d' ", mysqli_real_escape_string($this->connect() , $pub) , mysqli_real_escape_string($this->connect() , $id));  $res = $this->connect()->query($query);  if ($res)   {   return true;   }    else   {   return false;   }  } }?>