lic function setSeokey($value){ $this->seokey= $value;}public function setModule($id,$nomFr,$nomEn,$nomAr,$contenuFr,$contenuEn,$contenuAr,$image,$menu1,$moduleParent,$ordre,$type,$seotitle,$seodescription,$seokey){	$this->id = $id ;	$this->nomFr = $nomFr;	$this->nomEn = $nomEn;                $this->nomAr = $nomAr;	$this->contenuFr = $contenuFr;	$this->contenuEn = $contenuEn;                $this->contenuAr = $contenuAr;	$this->image = $image ;	$this->menu1 = $menu1 ;	$this->moduleParent = $moduleParent ;	$this->ordre = $ordre ;	$this->type = $type ; 	$this->seotitle = $seotitle ; 	$this->seodescription = $seodescription ; 	$this->seokey = $seokey ; }public function getSQL(){		$str = sprintf("nomFr='%s'",mysqli_real_escape_string($this->connect(),$this->getNomFr()));                $str .= sprintf(", nomEn='%s'",mysqli_real_escape_string($this->connect(),$this->getNomEn()));                                $str .= sprintf(", nomAr='%s'",mysqli_real_escape_string($this->connect(),$this->getNomAr()));                $str .= sprintf(", contenuFr='%s'",mysqli_real_escape_string($this->connect(),$this->getContenuFr()));                $str .= sprintf(", contenuEn='%s'",mysqli_real_escape_string($this->connect(),$this->getContenuEn()));                                $str .= sprintf(", contenuAr='%s'",mysqli_real_escape_string($this->connect(),$this->getContenuAr()));		$str .= sprintf(", image='%s'",mysqli_real_escape_string($this->connect(),$this->getImage()));		$str .= sprintf(", menu1='%d'",mysqli_real_escape_string($this->connect(),$this->getMenu1()));		$str .= sprintf(", moduleParent='%d'",mysqli_real_escape_string($this->connect(),$this->getModuleParent()));		$str .= sprintf(", ordre='%d'",mysqli_real_escape_string($this->connect(),$this->getOrdre()));		$str .= sprintf(", type='%s'",mysqli_real_escape_string($this->connect(),$this->getType()));                                $str .= sprintf(", seotitle=