on"],$fetch["seokey"]);                }                               			}		public function publishToDB($id,$pub){		$query = sprintf("update ".$_SESSION['pfx']."_module set menu1 = '%d' where id = '%d' ",mysqli_real_escape_string($this->connect(),$pub),mysqli_real_escape_string($this->connect(),$id));		$res = $this->connect()->query($query); 		if($res){ 			return true; 		}else{ 			return false; 		}	}	}?>